   this.velocity = 0;\r\n    this.acceleration = 0;\r\n    this.enabled = true;\r\n    this.targeting = false;\r\n    this.source = 0;\r\n    this.target = 0;\r\n    this.enableAnimation = true;\r\n} \r\n\r\nSoftFloat.prototype.enable = function() {\r\n    this.enabled = true;\r\n}\r\n\r\nSoftFloat.prototype.disable = function() {\r\n    this.enabled = false;\r\n}\r\n\r\nSoftFloat.prototype.update = function() {\r\n    if (!this.enabled) return false;\r\n    if (this.targeting) {\r\n        if(!this.enableAnimation){\r\n            this.value = this.target;\r\n            this.targeting = false;\r\n        } else {\r\n            this.acceleration += (this.ATTRACTION * (this.target - this.value));\r\n            this.acceleration += (this.target - this.value);\r\n            this.velocity = (this.velocity + this.acceleration) * this.DAMPING;\r\n            this.value += this.velocity;\r\n            //this.value += (this.target - this.value)/30;\r\n            this.acceleration = 0;\r\n            if (p5.abs(this.velocity) &gt; 0.0001) {\r\n                return true;\r\n            }\r\n            this.value = this.target;\r\n            this.targeting = false;\r\n        }\r\n    }\r\n    return false;\r\n}\r\n\r\nSoftFloat.prototype.setTarget = function(t) {\r\n    this.targeting = true;\r\n    this.target = t;\r\n    this.source = this.value;\r\n}\r\n\r\nSoftFloat.prototype.getTarget = function() {\r\n    return this.targeting ? this.target : this.value;\r\n}</pre>','Me, Me, Me! - Interactive Experiment','','publish','open','open','','me-me-me-interactive-experiment','','','2013-07-21 20:17:17','2013-07-21 20:17:17','',0,'http://thejsj.com/?p=1481',0,'post','',0),(1482,1,'2013-07-07 19:03:26','2013-07-07 19:03:26','','thumb','','inherit','open','open','','thumb','','','2013-07-07 19:03:26','2013-07-07 19:03:26','',1481,'http://thejsj.com/portfolio/wp-content/uploads/2013/07/thumb.png',0,'attachment','image/png',0),(1483,1,'2013-07-07 19:08:11','2013-07-07 19:0